    padding: 0;
    margin: 0;
}
*::after,*::after{
    box-sizing: border-box;
}


/*内容区域分配*/
.wrapper1{
    width: 100vw;
    min-height: 100vh;
    background: #d4ecf9;
    display: flex;
    justify-content: center;
    align-items: flex-start;
}
.content1{
    width: 200px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    position: relative;
    animation: translateY 2.5s linear alternate-reverse infinite;
}

/*头顶小风扇*/
.content1 .fan{
    display: flex;
    flex-direction: column;
    align-items: center;
    position: relative;
}
.content1 .fan .top{
    width: 20px;
    height: 10px;
    border-radius: 10px 10px 0 0;
    border: 2px solid #555555;
    background-color: #bd8d22;
}
.content1 .fan .line{
    width: 78px;
    height: 5px;
    border-radius: 2px;
    background-color: gray;
    opacity: .6;
    position: absolute;
    top: 8px;
    z-index: 3;
    animation: rotate3D .3s linear alternate infinite;
}
.content1 .fan .bottom{
    width: 7px;
    height: 9px;
    background-color: #bd8d22;
    border: 2px solid #000;
    border-bottom: 0;
}
@-moz-keyframes rotate3D {
    from{-moz-transform:rotate3D(0,1,0,0deg);}
    to{-moz-transform:rotate3D(0,1,0,360deg);}
}

@-ms-keyframes rotate3D {
    from{-ms-transform:rotate3D(0,1,0,0deg);}
    to{-ms-transform:rotate3D(0,1,0,360deg);}
}

@-o-keyframes rotate3D {
    from{-o-transform:rotate3D(0,1,0,0deg);}
    to{-o-transform:rotate3D(0,1,0,360deg);}
}

@keyframes rotate3D {
    from{transform:rotate3D(0,1,0,0deg);}
    to{transform:rotate3D(0,1,0,360deg);}
}
